mapper.select:
SELECT a.*,b.realname as tname,c.realname as sname,c.username as sUname,d.subject_name,e.file_path,e.original_name as file_name FROM yd_thesis a LEFT JOIN yd_subject_info d on a.subject_id = d.id LEFT join yd_teacher_info b on d.teacher_id =b.id LEFT join yd_student_info c on a.stu_id = c.id LEFT JOIN sys_file_manage e on e.id = a.file_id WHERE a.del_flag = '0' and c.class_id in ( SELECT c.id FROM yd_class_info c WHERE c.major_id in ( SELECT d.id from yd_major_info d LEFT JOIN yd_faculty_info e on d.faculty_id=e.id
WHERE e.id = #{queryName}
) )
and d.teacher_id = #{teacherId}
and b.realname LIKE CONCAT('%','${map.tname}','%')
and c.realname LIKE CONCAT('%','${map.sname}','%')
and a.state = #{map.state}
and c.username LIKE CONCAT('%','${map.suname}','%')
